#!/bin/bashPROJECTNAME="myblog"PROJECTBASE="."PROJECTBIN="$PROJECTBASE/bin"PROJECTLOGS="$PROJECTBASE/log"prog=$PROJECTNAMECURDIR=$(dirname $0)cd $CURDIRstart() {        echo -e "Begin to compile the project ---$PROJECTNAME..."        go build -o $PROJECTNAME main.go        chmod  777  "$CURDIR/$PROJECTNAME"        echo "Compilation completed"        echo "starting $PROJECTNAME,please waiting..."        nohup ./$PROJECTNAME > $PROJECTLOGS/run.log 2>&1 &        echo -e "ok"}stop(){        echo -e $"Stopping the project ---$prog: "        pid=$(ps -ef | grep $prog | grep -v grep | awk '{print $2}')        if [ "$pid" ]; then                echo -n $"kill process pid: $pid "                kill -9 $pid                ret=0                for ((i=1;i<=15;i++)); do                        sleep 1                        pid=$(ps -ef | grep $prog | grep -v grep | awk '{print $2}')                        if [ "$pid" ]; then                                kill -9 $pid                                ret=0                        else                                ret=1                                break                        fi                done                if [ "$ret" ]; then                        echo -e $"ok"                else                        echo -e $"no"                fi        else                echo -e $"no program process to stop"        fi}restart(){        stop        sleep 2        start}case "$1" instart)        $1        ;;stop)        $1        ;;restart)        $1        ;;*)        echo $"Usage: $0 {start|stop|restart} {development|testing|production}"        exit 2        ;;esac